(function ($) {
	// remove layerX and layerY
	var all = $.event.props,
			len = all.length,
			res = [];
	while (len--) {
		var el = all[len];
		if (el != 'layerX' && el != 'layerY') res.push(el);
	}
	$.event.props = res;

	var intervaloFancy;
	var aplicarFancy = function() {
		//console.log('aplicou fancy novo 3');
		var fancyC = $('.fancy_thumbnail_slider_container');
		$('.fancy_thumbnail, .fancy_thumbnail li').removeAttr('style').removeAttr('class');
		var conteudo = $('.fancy_thumbnail_slider_container').html();
		fancyC.html('');
		fancyC.html(conteudo);
		var numero = new Date().getTime();
		fancyC.find('ul').attr('class', 'fancy_thumbnail fancy'+numero);
		clearInterval(intervaloFancy);
		intervaloFancy = '';
		//console.log(intervaloFancy);
		$('.fancy'+numero).fancythumbnailslider();	
		//window.location.reload( true );
		$('body').css('overflow-y', 'auto');
	}

	$(document).ready(function() {		
		$('.fancy_thumbnail_starter').fancythumbnailslider();

	});


    $.fn.extend({
        fancythumbnailslider: function (options) {
            var defaults = {
                delay: 100,
                slide: 300,
                loop: true,
                autoplay: true,
                time: 3000,
                captionFade: 0,
                arrowKeys: false,
                shuffle: false,
                pause: false
            };
            var options = $.extend(defaults, options);
            return this.each(function () {
                o = options;
                $float_easing = "easeInOutQuart";
                $item_container = $(this);
                $li = $item_container.find("li");
                $next_button = $(".fancy_thumbnail_next");
                $prev_button = $(".fancy_thumbnail_prev");
                item_size = $li.size();
                item_width = parseInt($li.width(), 10);
                item_height = parseInt($li.height(), 10);
                full_item_width = item_width + parseInt($li.css("marginRight"), 10) + parseInt($li.css("marginLeft"), 10);
                full_item_height = item_height + parseInt($li.css("marginTop"), 10) + parseInt($li.css("marginBottom"), 10);
                adapt_to_window_size();
                set_item_classes();
                set_start_positions();
                set_next_prev_classes();
                $next_button.click(function () {
                    var this_class = $(this).attr("class");
                    if (this_class.search("disable") <= 0) {
                        clearInterval(intervaloFancy);
                        show_next_prev("next");
                    }
                    return (false);
                });
                $prev_button.click(function () {
                    var this_class = $(this).attr("class");
                    if (this_class.search("disable") <= 0) {
                        clearInterval(intervaloFancy);
                        show_next_prev("prev");
                    }
                    return (false);
                });
                $(window).resize(function () {
                    window_resize();
                });
                if (o.arrowKeys) {
                    $(document).keydown(function (event) {
                        if (event.which == 40) {
                            clearInterval(intervaloFancy);
                            show_next_prev("next");
                        } else {
                            if (event.which == 38) {
                                clearInterval(intervaloFancy);
                                show_next_prev("prev");
                            }
                        }
                    });
                }
                $(document).hover(function () {
                    var window_width = parseInt($(window).width(), 10);
                    var distance = parseInt($item_container.width(), 10) - window_width;
                    if ($item_container.width() > window_width) {
                        $(this).mousemove(function (e) {
                            var margin = ((distance / window_width));
                            var margin = Math.ceil((margin * e.pageX));
                            $item_container.css({
                                marginLeft: "-" + margin + "px"
                            });
                        });
                    }
                });
                if (o.autoplay) {
                    intervaloFancy = setInterval(function () {
                        var row_item_class = $item_container.find("li.first").attr("class");
                        row_item_class = row_item_class.replace(" first", "");
                        var row_id = row_item_class.split("_");
                        if (!o.loop) {
                            if (row_id[1] == total_rows - (rows - 1)) {
                                clearInterval(intervaloFancy);
                            } else {
                                show_next_prev("next");
                            }
                        } else {
                            show_next_prev("next");
                        }
                    }, o.time);
                }
            });

            function adapt_to_window_size() {
                var container_width = parseInt($item_container.width(), 10);
                var container_height = parseInt($item_container.height(), 10);
                var window_width = parseInt($(window).width(), 10);
                var new_container_width = (Math.ceil(window_width / full_item_width) + 1) * (full_item_width);
                $item_container.css("width", new_container_width + "px");
                columns = Math.floor(new_container_width / full_item_width);
                rows = Math.floor(container_height / full_item_height);
                visible_items = columns * rows;
                filled_rows = Math.ceil(item_size / columns);
                min_filled_rows = rows + 1;
                if (filled_rows < min_filled_rows) {
                    var needed_items = min_filled_rows * columns;
                    var missing_items = needed_items - item_size;
                    total_rows = min_filled_rows;
                } else {
                    var needed_items = filled_rows * columns;
                    var missing_items = needed_items - item_size;
                    total_rows = filled_rows;
                }
                if (missing_items > item_size) {
                    alert("There are not enough items! Scale down your browser window or add more items");
                } else {
                    for (var i = 1; i <= missing_items; i++) {
                        var item_inner_temp = $item_container.find("li:nth-child(" + i + ")").html();
                        $item_container.append("<li>" + item_inner_temp + "</li>");
                    }
                    var new_item_size = $item_container.find("li").size();
                    $li = $item_container.find("li");
                }
            }
            function set_item_classes() {
                $li.each(function (index) {
                    $item = $(this);
                    var row_number = Math.floor((index + columns) / columns);
                    var col_number = (index + columns) - (columns * row_number);
                    $item.addClass("row_" + row_number + "_col_" + col_number);
                    if (row_number == 1) {
                        $item.addClass("first");
                    }
                    if (row_number <= rows) {
                        $item.css({
                            display: "block",
                            opacity: 1
                        });
                    } else {
                        $item.css({
                            opacity: 0,
                            display: "none"
                        });
                    }
                });
            }
            function set_start_positions() {
                $li.each(function (index) {
                    $item = $(this);
                    var row_number = Math.floor(((index + columns) / columns) - 1);
                    var col_number = index - (columns * row_number);
                    if (row_number < rows) {
                        var top_position = row_number * full_item_height;
                    } else {
                        var top_position = rows * full_item_height;
                    }
                    var left_position = col_number * full_item_width;
                    $item.css("position", "absolute");
                    $item.css("top", top_position + "px");
                    $item.css("left", left_position + "px");
                });
            }
            function set_next_prev_classes() {
							  var first_row_class = $item_container.find("li.first:visible").attr("class");
								if(!first_row_class) {
									aplicarFancy();
									return false;
								}	
							  first_row_class = first_row_class.replace(" first", "");
                var row_id_first = first_row_class.split("_");
                row_id_first = parseInt(row_id_first[1], 10);
                if (!o.loop) {
                    if (row_id_first == "1") {
                        $prev_button.addClass("disable");
                    } else {
                        $prev_button.removeClass("disable");
                    }
                    if ((row_id_first - 1) + rows == total_rows) {
                        $next_button.addClass("disable");
                    } else {
                        $next_button.removeClass("disable");
                    }
                }
            }
            function window_resize() {
                $li.each(function (index) {
                    $item = $(this);
                    $item.removeClass();
                    if (index + 1 > item_size) {
                        $item.remove();
                    }
                });
                adapt_to_window_size();
                set_item_classes();
                set_start_positions();
            }
            function show_next_prev(method) {
                var first_row_class = $item_container.find("li.first:visible").attr("class");
								if(!first_row_class) {
									aplicarFancy();
									return false;
								}	
                first_row_class = first_row_class.replace(" first", "");
                var row_id_first = first_row_class.split("_");
                row_id_first = parseInt(row_id_first[1], 10);
                var row_array = new Array();
                for (var i = 0; i < rows + 1; i++) {
                    if (method == "next") {
                        id = row_id_first + i;
                        if (id > total_rows) {
                            id = id - total_rows;
                        }
                        row_array[i] = id;
                        if (o.loop) {
                            var top_position = rows * full_item_height;
                            if (i == rows) {
                                $item_container.find('li.[class^="row_' + id + '"]').css("top", top_position + "px");
                            }
                        }
                    } else {
                        if (method == "prev") {
                            id = (row_id_first) + (rows - 1 - i);
                            if (id <= 0) {
                                id = id + total_rows;
                            }
                            if (id > total_rows) {
                                id = id - total_rows;
                            }
                            row_array[i] = id;
                            if (o.loop) {
                                var top_position = -full_item_height;
                                if (i == rows) {
                                    $item_container.find('li.[class^="row_' + id + '"]').css("top", top_position + "px");
                                }
                            }
                        }
                    }
                }
                var shuffle_array = new Array();
                for (var i = 0; i < columns; i++) {
                    shuffle_array[i] = i;
                }
                if (o.shuffle) {
                    shuffle_array = $.shuffle(shuffle_array);
                }
                var do_action = true;
                if (row_id_first == "1" && method == "prev" || row_id_first == total_rows - (rows - 1) && method == "next") {
                    if (!o.loop) {
                        do_action = false;
                    }
                }
                if (do_action) {
                    for (var r = 0; r < row_array.length; r++) {
                        for (var c = 0; c < columns; c++) {
                            if (method == "next") {
                                var new_top_pos = (r - 1) * full_item_height;
                            } else {
                                if (method == "prev") {
                                    var new_top_pos = Math.abs((r - rows) * full_item_height);
                                }
                            }
                            var delay = ((shuffle_array[c]) * o.delay) + (o.delay * (r));
                            var $item = $item_container.find("li.row_" + row_array[r] + "_col_" + c);
                            $item.removeClass("first");
                            if (r == 0) {
                                $item.delay(delay).animate({
                                    top: new_top_pos,
                                    opacity: 0
                                }, o.slide, $float_easing, function () {
                                    $(this).css("display", "none");
                                });
                            } else {
                                if (r > 0 && r < rows) {
                                    if (method == "next") {
                                        if (r == 1) {
                                            $item.addClass("first");
                                        }
                                    }
                                    $item.delay(delay).animate({
                                        top: new_top_pos,
                                        opacity: 1
                                    }, o.slide, $float_easing, function () {});
                                } else {
                                    if (r == rows) {
                                        $item.css("display", "block");
                                        if (method == "prev") {
                                            $item.addClass("first");
                                        }
                                        $item.delay(delay).animate({
                                            top: new_top_pos,
                                            opacity: 1
                                        }, o.slide, $float_easing, function () {});
                                    }
                                }
                            }
                        }
                    }
                }
                set_next_prev_classes();
                $li.find(".caption").hide();
            }
        }
    });
})(jQuery);